INSERT INTO `psychomatrix_additional_contents` (`id`, `code`, `title`, `annotation`, `level`, `text`) VALUES
(1, '1-0', 'Самооценка', 'Значение первого столбца (1, 2, 3) определяет силу самооценки человека. В этом качестве заключено желание выделить себя из толпы, показаться более яркой личностью (ум, разговор, одежда, косметика и т. д.).', 0, 'Можно говорить о том, что человек имеет заниженную самооценку, недооценивает свои возможности. С тремя цифрами иногда “прозревает”, но не уверен в себе гораздо чаще.'),
(2, '1-1', 'Самооценка', 'Значение первого столбца (1, 2, 3) определяет силу самооценки человека. В этом качестве заключено желание выделить себя из толпы, показаться более яркой личностью (ум, разговор, одежда, косметика и т. д.).', 1, 'Можно говорить о том, что человек имеет заниженную самооценку, недооценивает свои возможности. С тремя цифрами иногда “прозревает”, но не уверен в себе гораздо чаще.'),
